import React from "react";
import {
  StyleSheet,
  Text,
  View,
  FlatList,
  TouchableOpacity,
  Image,
} from "react-native";
import { MaterialIcons } from "@expo/vector-icons";
import { EvilIcons } from "@expo/vector-icons";
import { AntDesign } from "@expo/vector-icons";
import { SliderBox } from "react-native-image-slider-box";
import { connect } from "react-redux";

function Details({ title, navigation, ctr, onIncrementCounter }) {
  const images = {
    images: [
      require("./assets/images/a2.png"),
      require("./assets/images/a2.png"),
      require("./assets/images/a2.png"),
    ],
  };

  const sectors = [
    { url: require("./assets/images/a1.png"), key: 1 },
    { url: require("./assets/images/a3.png"), key: 3 },
    { url: require("./assets/images/a4.png"), key: 4 },
    { url: require("./assets/images/a5.png"), key: 5 },
    { url: require("./assets/images/a6.png"), key: 6 },
  ];
  return (
    <View>
      <View style={styles.header}>
        <MaterialIcons
          name="menu"
          size={28}
          // onPress={openMenu}
          style={styles.icon1}
        />
        <EvilIcons style={styles.icon2} name="cart" size={27} />
        <AntDesign style={styles.icon3} name="search1" size={24} />
        <View>
          <Text style={styles.headerText}>{ctr}</Text>
        </View>
      </View>
      <SliderBox
        circleLoop
        dotColor="#D95500"
        sliderBoxHeight={250}
        images={images.images}
      />
      <View
        style={{
          display: "flex",
          flexDirection: "row",
          flexWrap: "wrap",
          alignContent: "center",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        {/* @@@ */}
        <TouchableOpacity onPress={() => navigation.navigate("IntroSlide")}>
          <Image style={styles.item} source={sectors[4].url} />
        </TouchableOpacity>
        <TouchableOpacity onPress={() => onIncrementCounter()}>
          <Image style={styles.item} source={sectors[1].url} />
        </TouchableOpacity>
      </View>
      <View
        style={{
          display: "flex",
          flexDirection: "row",
          flexWrap: "wrap",
          alignContent: "center",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <TouchableOpacity onPress={() => navigation.navigate("Meat")}>
          <Image style={styles.item} source={sectors[0].url} />
        </TouchableOpacity>
      </View>
      <View
        style={{
          display: "flex",
          flexDirection: "row",
          flexWrap: "wrap",
          alignContent: "center",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <TouchableOpacity>
          <Image style={styles.item} source={sectors[3].url} />
        </TouchableOpacity>
        <TouchableOpacity>
          <Image style={styles.item} source={sectors[2].url} />
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  header: {
    paddingTop: 53,
    width: "100%",
    backgroundColor: "#DDD",
    // height: "100%",
    flexDirection: "row",
    alignItems: "center",
    justifyContent: "center",
  },
  headerText: {
    fontWeight: "bold",
    fontSize: 20,
    color: "#333",
    letterSpacing: 1,
  },
  icon1: {
    position: "absolute",
    left: 20,
    bottom: 0,
    color: "#1d81c3",
    paddingBottom: 10,
  },
  icon2: {
    position: "absolute",
    right: 20,
    bottom: 0,
    color: "#1d81c3",
    paddingBottom: 10,
  },
  icon3: {
    position: "absolute",
    right: 58,
    bottom: 0,
    color: "#1d81c3",
    paddingBottom: 10,
  },
  container: {
    flex: 1,
    paddingTop: 40,
    paddingHorizontal: 20,
    backgroundColor: "#fff",
  },
  item: {
    margin: 5,
    borderRadius: 8,
  },
});

const mapStateToProps = (state) => {
  return {
    ctr: state.counter,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onIncrementCounter: () => dispatch({ type: "INCREMENT" }),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Details);
